mapper.select:
select count(1)
from dic_qua
where 1=1
and qua_name = #{quaName}

    and parent_id = #{parentId}


    and id != #{id}

select *
from dic_qua
where qua_code = #{quaCode}

select *
from dic_qua
where qua_name = #{quaName}
AND `parent_id` = #{parentId}

select *
from dic_qua
where id = #{pkid}

SELECT d.id, d.bench_name AS benchName, d.qua_code AS quaCode, d.parent_id AS parentId FROM biaodaa_admin.dic_qua d WHERE d.level = #{noticeLevel} and d.id=#{parentId}
and d.bench_name like CONCAT('%',#{benchName},'%')
order by order_no

SELECT d.id, d.qua_name as quaName, d.bench_name AS benchName, d.qua_code AS quaCode, d.parent_id AS parentId FROM biaodaa_admin.dic_qua d WHERE 1=1
and d.id=#{parentId}
and d.id=#{id}
order by order_no

SELECT d.id, d.qua_name as quaName, d.bench_name AS benchName, d.qua_code AS quaCode, d.parent_id AS parentId FROM biaodaa_admin.dic_qua d WHERE 1=1
and d.id=#{parentId}
order by order_no

SELECT d.id, d.qua_name AS quaName, d.qua_code AS quaCode, d.biz_type as bizType FROM biaodaa_admin.dic_qua d WHERE d.level = '1'
and d.id = #{id}
order by order_no

SELECT d.id, d.bench_name AS benchName, d.qua_code AS quaCode, d.qua_name as quaName, d.biz_type as bizType FROM biaodaa_admin.dic_qua d WHERE 1=1
and d.parent_id=#{zzIdOne}
and d.id = #{two}
