SELECT d.id, d.bench_name AS benchName, d.qua_code AS quaCode, d.qua_name as quaName, d.biz_type as bizType FROM biaodaa_admin.dic_qua d WHERE 1=1
and d.parent_id=#{zzIdOne}
and d.id = #{three}
order by d.order_no

SELECT d.id, d.bench_name AS benchName, d.qua_code AS quaCode, d.qua_name as quaName, d.biz_type as bizType, d.biz_type as bizType FROM biaodaa_admin.dic_qua d WHERE 1=1
and d.parent_id=#{zzIdOne}
and d.id = #{four}
