select * from (SELECT t6.`id`, t7.id as ailasId, t6.`qua_code` AS quaCode, CONCAT(IFNULL(t6.`bench_name`,''),IFNULL(t4.`name`,'')) AS quaLevel, CONCAT(IFNULL(t7.`name`,''),IFNULL(t3.`name`,'')) AS jointAilas, ifnull(t7.create_by,'system') AS createBy FROM `dic_alias` t3 JOIN `dic_common` t4 ON t3.`std_code` = t4.`code` RIGHT JOIN `rel_qua_grade` t5 ON t4.`code` = t5.`grade_code` RIGHT JOIN `dic_qua` t6 ON t5.`qua_code` = t6.`qua_code` JOIN `dic_alias` t7 ON t6.`qua_code` = t7.`std_code` WHERE 1=1 and t6.bench_name is not null ) qua where 1=1
and qua.quaLevel like CONCAT('%',#{benchName},'%')
and qua.jointAilas like CONCAT('%',#{qualAilas},'%')
and (${createBy})
order by t6.${rank}
LIMIT #{start}, #{pageSize}

SELECT t5.`id`, t7.id as ailasId, t3.id as levelAilasId, CONCAT( IFNULL(t6.`bench_name`, ''), IFNULL(t4.`name`, '') ) AS quaLevel, CONCAT( IFNULL(t7.`name`, ''), IFNULL(t3.`name`, '') ) AS jointAilas, IFNULL(t7.create_by, 'system') AS createBy, t7.`create_time` AS createTime FROM `dic_alias` t3 JOIN `dic_common` t4 ON t3.`std_code` = t4.`code` JOIN `rel_qua_grade` t5 ON t4.`code` = t5.`grade_code` JOIN `dic_qua` t6 ON t5.`qua_code` = t6.`qua_code` JOIN `dic_alias` t7 ON t6.`qua_code` = t7.`std_code` WHERE 1 = 1
AND t6.`id` = #{qualId}
and t6.qua_code = #{qualCode}
and t7.`name` = #{qualAilasName}
AND t3.`name` = #{levelAilasName}
and t4.`code` = #{levelCode}

SELECT t5.`id`, t7.id as ailasId, ifnull(t3.id,'') as levelAilasId, CONCAT( IFNULL(t6.`bench_name`, ''), IFNULL(t4.`name`, '') ) AS quaLevel, CONCAT( IFNULL(t7.`name`, ''), IFNULL(t3.`name`, '') ) AS jointAilas, IFNULL(t7.create_by, 'system') AS createBy, t7.`create_time` AS createTime FROM `dic_alias` t3 JOIN `dic_common` t4 ON t3.`std_code` = t4.`code` right JOIN `rel_qua_grade` t5 ON t4.`code` = t5.`grade_code` JOIN `dic_qua` t6 ON t5.`qua_code` = t6.`qua_code` JOIN `dic_alias` t7 ON t6.`qua_code` = t7.`std_code` WHERE 1 = 1
AND t6.`id` = #{qualId}
and t6.qua_code = #{qualCode}
and t7.`name` = #{qualAilasName}
AND t3.`name` = #{levelAilasName}
and t4.`code` = #{levelCode}
