mapper.select:
SELECT `pkid`, `title`, `pub_date` AS pubDate, `src_site` AS srcSite, `nt_status` AS ntStatus
FROM ${tableName}
WHERE 1=1 AND is_enable = 1

    AND provice_code = #{proviceCode}


    AND city_code = #{cityCode}


    AND county_code = #{countyCode}


    AND pub_date >= #{pubDate}


    AND pub_date <= #{pubEndDate}


    AND title LIKE CONCAT('%', #{title}, '%')


    AND nt_category = #{ntCategory}


    AND nt_status = #{ntStatus}

ORDER BY `pub_date` DESC, `nt_status` ASC
LIMIT #{start}, #{pageSize}

SELECT COUNT(*)
FROM ${tableName}
WHERE 1=1 AND is_enable = 1

    AND provice_code = #{proviceCode}


    AND city_code = #{cityCode}


    AND county_code = #{countyCode}


    AND pub_date >= #{pubDate}


    AND pub_date <= #{pubEndDate}


    AND title LIKE CONCAT('%', #{title}, '%')


    AND nt_category = #{ntCategory}


    AND nt_status = #{ntStatus}

SELECT main.title, CASE main.nt_status WHEN 0 THEN '未编辑' WHEN 1 then '已编辑' WHEN 2 then '已审核' WHEN 4 then '未通过' WHEN 5 then '已处理' ELSE '未审核' END AS nt_status, CONCAT(tenders.segment,'标段') AS segment, main.pub_date,
(SELECT `area_name` FROM sys_area WHERE area_code = main.city_code) AS city_code, (SELECT `area_name` FROM sys_area WHERE area_code = main.county_code) AS county_code,
(SELECT `name` FROM twf_dict WHERE `type` = 1 AND code = main.biness_type) AS biness_type, (SELECT `name` FROM twf_dict WHERE `type` = 4 AND code = tenders.pro_type) AS pro_type, '资质', tenders.controll_sum,
tenders.pro_sum, tenders.pro_duration, (SELECT `name` FROM dic_common WHERE `code` = tenders.pb_mode) AS pb_mode, tenders.bid_bonds, tenders.bid_bonds_end_time,
tenders.enroll_end_time,tenders.enroll_addr, tenders.audit_time, tenders.bid_end_time, tenders.opening_addr,
(SELECT `name` FROM twf_dict WHERE `type` = 6 AND code = tenders.filing_pfm) AS filing_pfm, (SELECT `name` FROM twf_dict WHERE `type` = 2 AND code = tenders.nt_td_status) AS nt_type, tenders.opening_person,
main.url
FROM tb_nt_mian_${source} AS main
LEFT JOIN tb_nt_tenders_${source} AS tenders ON main.pkid = tenders.nt_id
WHERE 1=1 AND main.is_enable = 1

    AND main.provice_code = #{proviceCode}


    AND main.city_code = #{cityCode}


    AND main.county_code = #{countyCode}


    AND main.pub_date >= #{pubDate}


    AND main.pub_date <= #{pubEndDate}


    AND main.title LIKE CONCAT('%', #{title}, '%')


    AND main.nt_category = #{ntCategory}


    AND nt_status = #{ntStatus}

ORDER BY pub_date DESC

mapper.update:
update ${tableName}
set


         is_enable = #{isEnable},


         is_enable = 0,


 `updated` = NOW(),
 `update_by` = #{updateBy}
WHERE pkid = #{pkid}

mapper.select:
SELECT nt_category AS ntCategory
FROM ${tableName}
WHERE pkid = #{pkid}

SELECT seg_count AS segCount
FROM ${tableName}
WHERE pkid = #{pkid}
